import React, { useState } from 'react'
import { Modal, Button, Form } from 'react-bootstrap';
import Alerts from '../Alert';
import axios from 'axios';
import { linkGetSellarDetails } from '../api_links';

interface LoginModalProps {
    show: boolean;
    handleClose: () => void;
  }

const ShowSellerDetails: React.FC<LoginModalProps> = ({ show, handleClose }) => {
    const [alertMessage, setAlertMessage] = useState<string | null>(null);
    const [alertVariant, setAlertVariant] = useState<string | undefined>(undefined);

    // fetch data using axios
    const handleSellerDetails = async (sellerId:number) =>{
        try {
          await axios.delete(linkGetSellarDetails.replace(':id',`${sellerId}`));
          setAlertMessage('Property deleted Successfully.');
          setAlertVariant('danger');
          
          
        } catch (error) {
          console.error('Error deleting post:', error);
          setAlertMessage('Property delete error.');
          setAlertVariant('danger');
        }
      };
   
  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Login</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div>
      <Alerts alertMessage={alertMessage} alertVariant={alertVariant} setAlertMessage={setAlertMessage}/>
      </div>
      
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        
          
        
      </Modal.Footer>
    </Modal>
  )
}

export default ShowSellerDetails
