import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import { linkGetAllProperties, linkGetSellerProperties } from '../components/api_links';
import axios from 'axios';
import {Card, Row, Col, Button, InputGroup, CardBody} from 'react-bootstrap';
import PropertyFormModal from './PropertyForm';

interface propertyProps{
    title:string;
    description:string;
    location:string;
    area:string;
    bedrooms:string;
    bathrooms:string;
    price:number;
    near_by:string;
    seller_id: Number;
};

const SellerProperties:React.FC = () => {
    const [showPropertyFormModal, setShowPropertyFormModal] = useState<boolean>(false);
    const handleShowPropertyFormModal = () => setShowPropertyFormModal(true);
    const handleClosePropertyFormModal = () => setShowPropertyFormModal(false);

    const [getAllProperties, setGetAllProperties] = React.useState<propertyProps[]>();
    const [sellerId, setSellerId] = React.useState<number | null>(20);

    React.useEffect(() => {
        fetchGetAllProperties();
    }, []);

    const fetchGetAllProperties = async () => {
        try {
            const response = await axios.get<propertyProps[]>(`${linkGetSellerProperties.replace(':id', `${sellerId}`)}`);
            setGetAllProperties(response.data);
            console.log(response.data);
        } catch (error) {
            console.error("Something went wrong, please try again", error);
        }
    };

  return (
    <Row xs={1} md={2} className="g-4">
        {getAllProperties && getAllProperties.map((property, index) =>(
            <Col key={index}>
          <Card>
            <Row className='g-0' style={{ width: '540px', height: '180px' }}>
                <Col className='col-md-4'>
                <Card.Img className='img-fluid rounded-start' src="images/img.jpg" />
                </Col>
            <Col className='col-md-8'>
            <CardBody>
              <Card.Title>{property.title}, â‚¹{property.price}</Card.Title>
              <Card.Text>{property.location} | {property.bedrooms} | sq.ft.{property.area}
              <br/><span className='text-muted'>Nearby:</span> {property.near_by}
              </Card.Text>
              <Card.Text>
                {property.description}
                <InputGroup className="m-1 ">
                <Button variant="outline-secondary" onClick={handleShowPropertyFormModal}>
                  Edit
                </Button>
                <PropertyFormModal show={showPropertyFormModal} handleClose={handleClosePropertyFormModal} />
                <Button variant="outline-danger" >
                Delete
                </Button>
              </InputGroup>
              </Card.Text>
              
              </CardBody>
            </Col>
            </Row>
          </Card>
           {/* ))} */}
        </Col>
      ))}
    </Row>
  )
}

export default SellerProperties
