import React, { useContext, useState } from 'react';
import { Modal, Button, Form, Alert } from 'react-bootstrap';
import { LoginData } from '../types/types';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { linkUserLogin } from './api_links';
import { AlertContext } from '../context/AlertContext';


interface LoginModalProps {
  show: boolean;
  handleClose: () => void;
}

const LoginModal: React.FC<LoginModalProps> = ({ show, handleClose }) => {
    const [alertMessage, setAlertMessage] = useState<string | null>(null);
    const [alertVariant, setAlertVariant] = useState<string | undefined>(undefined);
    const [formData, setFormData] = useState<LoginData>({
      email: '',
      password: '',
      user_type: 1,
    });
    const navigate = useNavigate();
    const alertContext = useContext(AlertContext);
    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
      const { name, value } = e.target;
      setFormData({
        ...formData,
        [name]: value,
      });
    };

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
        const response = await axios.post(linkUserLogin, formData);
        console.log('User registered:', response.data);
        if (response.status === 401) {
          // navigate('/');
          setAlertMessage('Invalid Credentials!');
          setAlertVariant('danger');
        }
         else {
          setAlertMessage('Logged in successfully');
          setAlertVariant('success');
          if (response.data.user_type === "seller"){
            navigate('/api/seller-dashboard');            
          }
          else{
            navigate('/api/properties');            
          }
        }
      } catch (error) {
        console.error('There was an error registering the user:', error);
        if (axios.isAxiosError(error) && error.response) {
          if (error.response.status === 401) {
            setAlertMessage('Invalid Credentials!');
            setAlertVariant('danger');
          } else {
            setAlertMessage('An error occurred while Login.');
            setAlertVariant('danger');
          }
        } else {
          setAlertMessage('An error occurred while login.');
          setAlertVariant('danger');
        }
      }
    handleClose();
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Login</Modal.Title>
      </Modal.Header>
      <Modal.Body>
      
      <form onSubmit={handleLogin} className="container mt-5">
        
        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email</label>
          <input
            type="email"
            className="form-control"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required />
        </div>
        
        <div className="mb-3">
          <label htmlFor="password" className="form-label">Password</label>
          <input
            type="password"
            className="form-control"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required />
        </div>
        <div className="mb-3">
          <label htmlFor="user_type" className="form-label">User Type</label>
          <select
            className="form-select"
            id="user_type"
            name="user_type"
            value={formData.user_type}
            onChange={handleChange}
            required
          >
            <option value="1">Buyer</option>
            <option value="2">Seller</option>
          </select>
        </div>
      </form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleLogin}>
          Login
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default LoginModal;
