import axios from 'axios';
import {Card, Row, Col, CardBody} from 'react-bootstrap';

interface propertyProps{
    title:string;
    description:string;
    location:string;
    area:string;
    bedrooms:string;
    bathrooms:string;
    price:number;
    near_by:string;
};

const SellerProperties:React.FC = () => {
    const [getAllProperties, setGetAllProperties] = React.useState<propertyProps[]>();

    React.useEffect(() => {
        fetchGetAllProperties();
    }, []);

    // fetching the all properties using async await
    const fetchGetAllProperties = async() => {
        try{
            const response = await fetch(linkGetAllProperties)
            const allPrperties = await response.json();
            setGetAllProperties(allPrperties)
            console.log(allPrperties);
       
        } catch (error) {
            console.error("somathing went wrong please try again", error);
        }
    };

  return (
    <Row xs={1} md={2} className="g-4">
        {getAllProperties && getAllProperties.map((property, index) =>(
