    title:string;
    description:string;
    location:string;
    area:string;
    bedrooms:string;
    bathrooms:string;
    price:number;
    near_by:string;
};

const PropertiesList:React.FC = () => {

    const [getAllProperties, setGetAllProperties] = React.useState<propertyProps[]>();

