import React, { useState } from 'react'
import {Form, Button, InputGroup} from 'react-bootstrap';
import { SearchData } from '../types/types';
import { linkSearchProperties } from './api_links';
import axios from 'axios';

const SearchForm = () => {
    const [searchFormData, setSearchFormData] = useState<SearchData>({
        location: '',
        bedrooms: '',
        search_data: ''
      });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
      const { name, value } = e.target;
      setSearchFormData({
        ...searchFormData,
        [name]: value,
      });
    };


    const handleSearch = async (e: React.FormEvent) => {
        e.preventDefault();
        
        
    
        // fetching the search properties using axios
        const fetchSearchProperties = () =>{
                axios.get(linkSearchProperties)
                .then((response) => {
                    console.log(response.data)
                    setSearchFormData(response.data)
                })
                .catch((error) =>{
                    console.error("somthing went wrong", error)
                })
            }
        };
  return (
    <>
    <form onSubmit={handleSearch} className="container mt-5">
        <InputGroup className="mb-3">
            <div className="mb-3">
                    <select
                        className="form-select"
                        id="user_type"
                        name="user_type"
                        value={searchFormData.locations}
                        onChange={handleChange}
                        required
                        >
                            <option value="1">Buyer</option>
                            <option value="2">Seller</option>
                    </select>
                </div>
            <Button variant="outline-secondary" onClick={handleSearch}>
              Search
            </Button>
        </InputGroup>
        <InputGroup className="mb-3">
            <input
                placeholder="Search"
                aria-label="Search"
                type="text"
                className="form-control"
                id="firstName"
                name="firstName"
                value={searchFormData.search_data}
                onChange={handleChange}
                required />
            <Button variant="outline-secondary" onClick={handleSearch}>
              Search
            </Button>
        </InputGroup>
    </form>
    </>
  )
}

export default SearchForm
