import React from 'react'
import {Form, Button, InputGroup} from 'react-bootstrap';

const SearchForm = () => {
    const [formData, setFormData] = useState<RegistrationData>({
        location: '',
        bedrooms: '',
        search_data: ''
      });



    const handleSearch:React.FC = () => {
        const [getAllProperties, setGetAllProperties] = React.useState<propertyProps[]>();
    
        React.useEffect(() => {
            fetchGetAllProperties();
        }, []);
    
        // fetching the all properties using async await
        const fetchGetAllProperties = async() => {
            try{
                const response = await fetch(linkGetAllProperties)
                const allPrperties = await response.json();
                setGetAllProperties(allPrperties)
                console.log(allPrperties);
           
            } catch (error) {
                console.error("somathing went wrong please try again", error);
            }
        };
  return (
    <>
    <form onSubmit={handleSearch} className="container mt-5">
    <InputGroup className="mb-3">
    <div className="mb-3">
                      <label htmlFor="firstName" className="form-label">First Name</label>
                      <input
                          type="text"
                          className="form-control"
                          id="firstName"
                          name="firstName"
                          value={formData.firstName}
                          onChange={handleChange}
                          required />
                  </div>
        <Form.Control
          placeholder="Recipient's username"
          aria-label="Recipient's username"
          aria-describedby="basic-addon2"
        />
        <Button variant="outline-secondary" onClick={handleSearch}>
          Search
        </Button>
      </InputGroup>
      </form>
    </>
  )
}
}
export default SearchForm
